import React, { Component } from "react";
import Layout from "../components/Layout";
import projhub from "../ethereum/projhub";
import web3 from "../ethereum/web3";
import { Grid, Form, Input } from "semantic-ui-react";

class ProjIndex extends Component {
  state = {
    account: "",
    manager: "",
    isUser: false
  };

  async componentDidMount() {
    const accs = await web3.eth.getAccounts();
    const manager = await projhub.methods.manager().call();
    const isUser = await projhub.methods.isUser(accs[0]).call();

    this.setState({ account: accs[0], manager: manager, isUser: isUser });
  }

  render() {

    return (
      <div>
        <Layout>
          <Grid>
            <Grid.Column width="10" style={{marginRight:"20px"}}>
              <h2>{this.state.account}</h2>
            </Grid.Column>
            <Grid.Column width="6">
              <Form>
                <Form.Field inline>
                  <label>Name</label>
                  <Input />
                </Form.Field>
              </Form>
            </Grid.Column>
          </Grid>
        </Layout>
      </div>
    );
  }
}

export default ProjIndex;
