import React, { Component } from "react";
import Layout from "../../components/Layout";
import projhub from "../../ethereum/projhub";
import web3 from "../../ethereum/web3";
import { Container, Form, Input, Button } from "semantic-ui-react";
const fs = require("fs");

class NewProject extends Component {
  state = {
    projName: "",
    file1: "",
    file2: "",
    fileBuffer: []
  };

  onSubmit = async () => {
    console.log(this.state.fileBuffer);
  }

  captureFile = event => {
    // event.stopPropogation();
    event.preventDefault();

    const file = event.target.files[0];
    this.setState({ file1: URL.createObjectURL(file) });
    console.log(file);

    let reader = new window.FileReader();
    reader.readAsArrayBuffer(file);
    reader.onloadend = () => this.convertToBuffer(reader);
  }

  convertToBuffer = async reader => {
    const buffer = await Buffer.from(reader.result);
    let fileBuffer = this.state.fileBuffer;
    fileBuffer.push(buffer);
    this.setState({ fileBuffer: fileBuffer });
  }

  render() {
    return (
      <Layout>
        <Container>
          <Form>
            <Form.Field>
              <label style={{ fontSize: "15px" }}>Project Name</label>
              <Form.Input
                fluid
                style={{ fontSize: "15px" }}
                placeholder="Project Name"
                value={this.state.projName}
                onChange={event =>
                  this.setState({ projName: event.target.value })
                }
              />
            </Form.Field>
            <Form.Field>
              <label style={{ fontSize: "15px" }}>Upload File 1</label>
              <Form.Input
                style={{ fontSize: "15px" }}
                type="file"
                onChange={event => this.captureFile(event)}
              />
            </Form.Field>
            <Form.Field>
              <label style={{ fontSize: "15px" }}>Upload File 2</label>
              <Form.Input
                style={{ fontSize: "15px" }}
                type="file"
                onChange={event => this.captureFile(event)}
              />
            </Form.Field>
            <Button color="teal" fluid onClick={this.onSubmit}/>
          </Form>
        </Container>
      </Layout>
    );
  }
}

export default NewProject;
