import React, { Component } from "react";
import Layout from "../../../components/Layout";
import projhub from "../../../ethereum/projhub";
import web3 from "../../../ethereum/web3";
import ipfs from "../../../ethereum/ipfs";
import { Container, Form, Input, Button } from "semantic-ui-react";
import { Router, Link } from "../../../routes";

class ShowFile extends Component {

  state = {
    fileHash: ""
  }

  static getInitialProps(props) {
    return {
      fileHash: props.query.hash
    };
  }

  async componentDidMount() {
    const fileHash = this.props.fileHash;
    const data = await ipfs.files.get(fileHash);
    console.log(data[0].content);

    this.setState({ fileHash: fileHash });
  }

  render() {
    return (
      <Layout>
        <h3>{this.state.fileHash}</h3>
      </Layout>
    );
  }
}

export default ShowFile;
