import React, { Component } from "react";
import Layout from "../components/Layout";
import projhub from "../ethereum/projhub";
import web3 from "../ethereum/web3";
import { Grid, Form, Input } from "semantic-ui-react";

class ProjIndex extends Component {
  state = {
    account: "",
    manager: "",
    isUser: false,
    name: "",
    email: ""
  };

  async componentDidMount() {
    const accs = await web3.eth.getAccounts();
    const manager = await projhub.methods.manager().call();
    const isUser = await projhub.methods.isUser(accs[0]).call();
    console.log(isUser);
    this.setState({ account: accs[0], manager: manager, isUser: isUser });
  }

  render() {

    return (
      <div>
        <Layout>
          <Grid style={{margin:"10px"}}>
            <Grid.Column width="10">
              <h2>{this.state.account}</h2>
            </Grid.Column>
            <Grid.Column width="6" style={{border:"2px solid", borderColor:"grey"}}>
              <Form>
                <Form.Field inline>
                  <label>Name</label>
                  <Input 
                    disabled={this.state.isUser} 
                    fluid
                    value={this.state.name}
                    onChange={event => this.setState({ name: event.target.value })}
                  />
                </Form.Field>
                <Form.Field inline>
                  <label>Email</label>
                  <Input 
                    disabled={this.state.isUser} 
                    fluid
                    value={this.state.email}
                    onChange={event => this.setState({ email: event.target.value })}
                  />
                </Form.Field>
              </Form>
            </Grid.Column>
          </Grid>
        </Layout>
      </div>
    );
  }
}

export default ProjIndex;
