import React, { Component } from "react";
import Layout from "../components/Layout";
import projhub from "../ethereum/projhub";
import web3 from "../ethereum/web3";
import { Grid, Form, Input, Button } from "semantic-ui-react";

class ProjIndex extends Component {
  state = {
    account: "",
    manager: "",
    isUser: false,
    name: "",
    email: ""
  };

  async componentDidMount() {
    const accs = await web3.eth.getAccounts();
    const manager = await projhub.methods.manager().call();
    const isUser = await projhub.methods.isUser(accs[0]).call();
    console.log(isUser);
    this.setState({ account: accs[0], manager: manager, isUser: isUser });
  }

  createUser = async () => {
    const name = this.state.name;
    const email = this.state.email;
    const acc = this.state.account;

    try {
      await projhub.methods
        .createUser(name, email)
        .send({ from: acc});

      console.log("User Registered");
    }
    catch(err) {
      console.log(err.message);
    }

  }

  render() {
    return (
      <div>
        <Layout>
          <Grid style={{ margin: "10px" }}>
            <Grid.Column width="12">
              <h2>{this.state.account}</h2>
            </Grid.Column>
            <Grid.Column
              width="4"
              style={{ border: "2px solid", borderColor: "grey" }}
            >
              <Form>
                <Form.Field inline>
                  <label style={{ width: "15%" }}>Name:</label>
                  <Input
                    style={{ width: "70%" }}
                    placeholder="Name"
                    disabled={this.state.isUser}
                    value={this.state.name}
                    onChange={event =>
                      this.setState({ name: event.target.value })
                    }
                  />
                </Form.Field>
                <Form.Field inline>
                  <label style={{ width: "15%" }}>Email:</label>
                  <Input
                    style={{ width: "70%" }}
                    placeholder="Email"
                    disabled={this.state.isUser}
                    value={this.state.email}
                    onChange={event =>
                      this.setState({ email: event.target.value })
                    }
                  />
                </Form.Field>
                <Button
                  fluid
                  color="teal"
                  size="medium"
                  content="Sign Up"
                  onClick={this.createUser}
                />
              </Form>
            </Grid.Column>
          </Grid>
        </Layout>
      </div>
    );
  }
}

export default ProjIndex;
