import React, { Component } from "react";
import Layout from "../../components/Layout";
import projhub from "../../ethereum/projhub";
import web3 from "../../ethereum/web3";
import { Container, Form, Input } from "semantic-ui-react";

class NewProject extends Component {
  state = {
    projName: "",
    file1: "",
    file2: ""
  };

  captureFile = event => {
    event.stopPropogation();
    event.preventDefault();

    this.setState({ file1: URL.createObjectURL(event.target.files[0]) });

  }

  render() {
    return (
      <Layout>
        <Container>
          <Form>
            <Form.Field>
              <label style={{ fontSize: "15px" }}>Project Name</label>
              <Form.Input
                fluid
                style={{ fontSize: "15px" }}
                placeholder="Project Name"
                value={this.state.projName}
                onChange={event =>
                  this.setState({ projName: event.target.value })
                }
              />
            </Form.Field>
            <Form.Field>
              <label style={{ fontSize: "15px" }}>Upload File 1</label>
              <Form.Input
                style={{ fontSize: "15px" }}
                type="file"
                onChange={event => this.captureFile(event)}
              />
            </Form.Field>
            <Form.Field>
              <label style={{ fontSize: "15px" }}>Upload File 2</label>
              <Form.Input
                style={{ fontSize: "15px" }}
                type="file"
                onChange={event => this.captureFile(event)}
              />
            </Form.Field>
          </Form>
        </Container>
      </Layout>
    );
  }
}

export default NewProject;
