import React, { Component } from "react";
import Layout from "../../../components/Layout";
import projhub from "../../../ethereum/projhub";
import web3 from "../../../ethereum/web3";
import ipfs from "../../../ethereum/ipfs";
import { Grid, Container } from "semantic-ui-react";

class DisplayUser extends Component {
  state = {
    userIndex: 0,
    userName: "",
    userAddress: "",
    userEmail: "",
    userProjCount: 0
  };

  static async getInitialProps(props) {
    return {
      userIndex: props.query.index
    };
  }

  async componentDidMount() {
    const userIndex = this.props.userIndex;
    const userDetails = await projhub.methods.users(userIndex).call();
    this.setState({
      userIndex: userIndex,
      userAddress: userDetails[1],
      userName: userDetails[2],
      userEmail: userDetails[3],
      userProjCount: userDetails[4]
    });
    console.log(userIndex);
  }

  render() {
    return (
      <Layout>
        <Container>
          <Grid>
            <Grid.Column width="6" style={{ marginTop:"20px", border: "1px solid", padding:"10px" }}>
              <div style={{whiteSpace:"nowrap", overflow:"hidden", textOverflow:"ellipsis"}}>
                <h2>{this.state.userName}</h2>
                <p style={{fontSize:"15px"}}>Address: {this.state.userAddress}</p>
                <p style={{fontSize:"15px"}}>Email: {this.state.userEmail}</p>
              </div>
            </Grid.Column>
            <Grid.Column width="10"></Grid.Column>
          </Grid>
        </Container>
      </Layout>
    );
  }
}

export default DisplayUser;
