import React, { Component } from "react";
import Layout from "../../../components/Layout";
import projhub from "../../../ethereum/projhub";
import web3 from "../../../ethereum/web3";
import ipfs from "../../../ethereum/ipfs";
import { Container, Form, Input, Button } from "semantic-ui-react";
import { Router, Link } from "../../../routes";

class ShowFile extends Component {
  state = {
    fileHash: "",
    fileContent: "",
    projName: ""
  };

  static getInitialProps(props) {
    return {
      fileHash: props.query.hash
    };
  }

  async componentDidMount() {
    const fileHash = this.props.fileHash;
    const data = await ipfs.files.get(fileHash);
    console.log(data[0].content.toString());

    this.setState({
      fileHash: fileHash,
      fileContent: data[0].content.toString()
    });
  }

  render() {
    return (
      <Layout>
        <Container>
          <h3>{this.state.fileHash}</h3>

          <div style={{border:"1px solid", borderColor:"grey", padding:"20px"}}>
            {this.state.fileContent.split("\n").map((line, key) => {
              return <p key={key} style={{fontSize:"16px", whiteSpace:"break-spaces"}}>{line}</p>;
            })}
          </div>
          <br/>
          <br/>
        </Container>
      </Layout>
    );
  }
}

export default ShowFile;
