import React, { Component } from "react";
import Layout from "../../components/Layout";
import projhub from "../../ethereum/projhub";
import web3 from "../../ethereum/web3";
import { Router, Link } from "../../routes";
import { List, Container } from "semantic-ui-react";

class ShowProject extends Component {
  state = {
    projId: 0,
    projName: "",
    projTime: "",
    projFiles: [],
    files: []
  };

  static async getInitialProps(props) {
    return {
      projId: props.query.id
    };
  }

  async componentDidMount() {
    const projId = this.props.projId;
    const accounts = await web3.eth.getAccounts();
    const projDetails = await projhub.methods
      .getProjectDetails(accounts[0], projId)
      .call();

    this.setState({
      projId: projId,
      projName: projDetails[1],
      projTime: projDetails[2],
      projFiles: projDetails[3]
    });
    console.log(projId);

    this.renderFiles();
  }

  renderFiles = async () => {
    const files = this.state.projFiles.map((file, index) => {
      return (
        <List.Item
          key={index}
          style={{
            padding: "10px",
            margin: "10px",
            border: "1px solid",
            borderColor: "grey"
          }}
        >
          <List.Content>
            <List.Header style={{ marginBottom: "10px", fontSize: "18px" }}>
              <Link route={`/projects/${this.state.projId}/${file}`}>
                <a>{file}</a>
              </Link>
            </List.Header>
          </List.Content>
        </List.Item>
      );
    });
    this.setState({ files: files });
  };

  render() {
    return (
      <Layout>
        <Container>
          <h5>{this.state.projName}</h5>
          <List items={this.state.files} />
        </Container>
      </Layout>
    );
  }
}

export default ShowProject;
