import React, { Component } from "react";
import Layout from "../../components/Layout";
import projhub from "../../ethereum/projhub";
import web3 from "../../ethereum/web3";
import { Router, Link } from "../../routes";
import { List, Container } from "semantic-ui-react";

class ShowProject extends Component {
  state = {
    projId: 0,
    projName: "",
    projTime: "",
    projFileHashes: [],
    projDescription: "",
    projFileNames: [],
    files: []
  };

  static getInitialProps(props) {
    return {
      projId: props.query.id
    };
  }

  async componentDidMount() {
    const projId = this.props.projId;
    const accounts = await web3.eth.getAccounts();
    const projDetails = await projhub.methods
      .getProjectDetails(accounts[0], projId)
      .call();

    this.setState({
      projId: projId,
      projName: projDetails[1],
      projTime: projDetails[2],
      projDescription: projDetails[3],
      projFileHashes: projDetails[4],
      projFileNames: projDetails[5]
    });
    console.log(projId);

    this.renderFiles();
  }

  renderFiles = async () => {
    const files = this.state.projFileNames.map((file, index) => {
      let hash = this.state.projFileHashes[index];
      return (
        <List.Item
          key={index}
          style={{
            padding: "10px",
            margin: "10px",
            border: "1px solid",
            borderColor: "lightgrey"
          }}
        >
          <List.Content>
            <List.Header style={{ marginBottom: "10px", fontSize: "18px" }}>
              <Link route={`/projects/${this.state.projId}/${hash}`} params={{projName: this.state.projName}}>
                <a>{file}</a>
              </Link>
            </List.Header>
          </List.Content>
        </List.Item>
      );
    });
    this.setState({ files: files });
  };

  render() {
    return (
      <Layout>
        <Container>
          <h3 style={{marginLeft:"10px"}}>{this.state.projName}</h3>
          <List items={this.state.files} />
          <br/>
          <div style={{margin:"10px", padding:"10px", border:"1px solid", borderColor:"lightgrey"}}>
            <h4>Description</h4>
            <hr style={{ backgroundColor:"lightgrey", height:"1px"}}/>
            {this.state.projDescription}
          </div>
        </Container>
      </Layout>
    );
  }
}

export default ShowProject;
