import React, { Component } from "react";
import Layout from "../../../components/Layout";
import projhub from "../../../ethereum/projhub";
import web3 from "../../../ethereum/web3";
import ipfs from "../../../ethereum/ipfs";
import { Container, Form, Input, Button } from "semantic-ui-react";
import { Router, Link } from "../../../routes";

class ShowFile extends Component {
  state = {
    projId: 0,
    fileIndex: 0,
    fileHash: "",
    fileContent: "",
    fileName: "",
    userAcc: "",
    projName: ""
  };

  static async getInitialProps(props) {
    return {
      projIndex: props.query.pid,
      fileIndex: props.query.fid
    };
  }

  async componentDidMount() {
    console.log(this.props.projIndex);
    const projId = this.props.projIndex;
    const fileIndex = this.props.fileIndex;
    const accounts = await web3.eth.getAccounts();
    const projDetails = await projhub.methods.getProjectDetails(accounts[0], projId).call();
    const projName = projDetails[1];
    const fileHash = projDetails[4][fileIndex];
    const fileName = projDetails[5][fileIndex];
    const data = await ipfs.files.get(fileHash);
    console.log(data[0].content.toString());

    this.setState({
      projId: projId,
      projName: projName,
      fileIndex: fileIndex,
      fileName: fileName,
      userAcc: accounts[0],
      fileHash: fileHash,
      fileContent: data[0].content.toString()
    });
  }

  render() {
    return (
      <Layout>
        <Container>
          <h3>{this.state.projName} / {this.state.fileName}</h3>

          <div style={{border:"1px solid", borderColor:"grey", padding:"20px"}}>
            {this.state.fileContent.split("\n").map((line, key) => {
              return <p key={key} style={{marginBottom:"5px", fontSize:"16px", whiteSpace:"break-spaces"}}>{line}</p>;
            })}
          </div>
          <br/>
          <br/>
        </Container>
      </Layout>
    );
  }
}

export default ShowFile;
