import React, { Component } from "react";
import Layout from "../components/Layout";
import projhub from "../ethereum/projhub";
import web3 from "../ethereum/web3";

class ProjIndex extends Component {
  state = {
    account: "",
    manager: ""
  };

  async componentDidMount() {
    const accs = await web3.eth.getAccounts();
    const manager = await projhub.methods.manager().call();

    this.setState({ account: accs[0], manager: manager });
  }

  render() {
    return (
      <div>
        <Layout>
          <h1>{this.state.manager}</h1>
          <h1>{this.state.account}</h1>
        </Layout>
      </div>
    );
  }
}

export default ProjIndex;
